import React from "react";
import { useCurrentFrame } from "remotion";
import { colors } from "../design/colors";
import { fontFamily } from "../design/fonts";
import { CursorBlink } from "./CursorBlink";

export const TerminalLine: React.FC<{
  text: string;
  prompt?: string;
  startFrame: number;
  charsPerFrame?: number;
  color?: string;
  showCursor?: boolean;
}> = ({
  text,
  prompt = "$ ",
  startFrame,
  charsPerFrame = 0.5,
  color = colors.text,
  showCursor = true,
}) => {
  const frame = useCurrentFrame();

  if (frame < startFrame) return null;

  const elapsed = frame - startFrame;
  const charCount = Math.min(text.length, Math.floor(elapsed * charsPerFrame));
  const typed = text.slice(0, charCount);
  const isDone = charCount >= text.length;

  return (
    <div
      style={{
        fontFamily: fontFamily.mono,
        fontSize: 20,
        lineHeight: 1.6,
        display: "flex",
        flexWrap: "wrap",
      }}
    >
      {prompt && (
        <span style={{ color: colors.green, fontWeight: 700 }}>{prompt}</span>
      )}
      <span style={{ color }}>{typed}</span>
      {showCursor && !isDone && <CursorBlink color={colors.green} fontSize={20} />}
    </div>
  );
};

