import { fontFamily } from "../design/fonts";

const LOG_ENTRIES = [
  { hash: "a3f7b2c", msg: "fix: resolve race condition in payment flow", branch: "fix/payments-race" },
  { hash: "e91d4fa", msg: "feat: add OAuth2 provider integration", branch: "feature/oauth2" },
