  speed?: number;
}> = ({ speed = 2 }) => {
  const frame = useCurrentFrame();

  const scrollY = interpolate(frame, [0, 120], [0, -500], {
    extrapolateRight: "clamp",
  });

  return (
    <div
      style={{
        overflow: "hidden",
        height: "100%",
        width: "100%",
