import React from "react";
import {
  AbsoluteFill,
  useCurrentFrame,
  interpolate,
} from "remotion";
import { colors } from "../design/colors";
import { fontFamily } from "../design/fonts";
import { SlackUI } from "../components/SlackUI";
import { SlackMessage } from "../components/SlackMessage";

// Scene 4: 210 frames (24-31s)
// - Frames 0-5: Instant cut to Slack UI
// - Frames 15-90: Reply typewriters in
// - Frames 90-100: Message sends
// - Frames 140-160: Manager replies with thumbs up
// - Frames 160-210: Hold on messages

export const Scene4Reversal: React.FC = () => {
  const frame = useCurrentFrame();

  const opacity = interpolate(frame, [0, 5], [0, 1], {
    extrapolateLeft: "clamp",
    extrapolateRight: "clamp",
  });

  return (
    <AbsoluteFill
      style={{
        backgroundColor: colors.black,
        justifyContent: "center",
        alignItems: "center",
      }}
    >
      <div style={{ opacity }}>
        <SlackUI>
          {/* Original manager message (already visible) */}
          <SlackMessage
            name="Sarah Chen"
            avatar="SC"
            text="Performance review today. Let's go through what you've been doing for the last 3 months."
            startFrame={0}
            charsPerFrame={100}
            avatarColor={colors.slackYellow}
          />

          {/* Your reply */}
          <SlackMessage
            name="You"
            avatar="Y"
            text="Over the last 3 months I shipped payments, implemented authentication, fixed production bugs, and improved API performance."
            startFrame={15}
            charsPerFrame={0.8}
            avatarColor={colors.slackGreen}
          />

          {/* Manager thumbs up */}
          {frame >= 140 && (
            <ManagerReaction frame={frame} startFrame={140} />
          )}
        </SlackUI>
      </div>
    </AbsoluteFill>
  );
};

const ManagerReaction: React.FC<{ frame: number; startFrame: number }> = ({
  frame,
  startFrame,
}) => {
  const elapsed = frame - startFrame;
  const opacity = interpolate(elapsed, [0, 10], [0, 1], {
    extrapolateLeft: "clamp",
    extrapolateRight: "clamp",
  });
  const scale = interpolate(elapsed, [0, 8], [0.5, 1], {
    extrapolateLeft: "clamp",
    extrapolateRight: "clamp",
  });

  return (
    <div
      style={{
        display: "flex",
        gap: 12,
        alignItems: "flex-start",
        marginLeft: 52,
      }}
    >
      <div
        style={{
          display: "flex",
          gap: 12,
          alignItems: "center",
        }}
      >
        <div
          style={{
            width: 40,
            height: 40,
            borderRadius: 6,
            backgroundColor: colors.slackYellow,
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            fontFamily: fontFamily.display,
            fontSize: 18,
            fontWeight: 700,
            color: colors.white,
            flexShrink: 0,
          }}
        >
          SC
        </div>
        <div style={{ display: "flex", flexDirection: "column", gap: 4 }}>
          <div style={{ display: "flex", alignItems: "baseline", gap: 8 }}>
            <span
              style={{
                color: colors.white,
                fontFamily: fontFamily.display,
                fontWeight: 700,
                fontSize: 15,
              }}
            >
              Sarah Chen
            </span>
            <span
              style={{
                color: colors.secondary,
                fontFamily: fontFamily.display,
                fontSize: 12,
              }}
            >
              10:34 AM
            </span>
          </div>
          <div
            style={{
              opacity,
              transform: `scale(${scale})`,
              transformOrigin: "left center",
              fontSize: 32,
            }}
          >
            {"👍"}
          </div>
        </div>
      </div>
    </div>
  );
};

