  useVideoConfig,
  spring,
  interpolate,
  Sequence,
} from "remotion";
import { colors } from "../design/colors";
import { fontFamily } from "../design/fonts";
import { CursorBlink } from "../components/CursorBlink";

// Scene 5: 120 frames (31-35s)
// - Frames 10-50: "You ship across too many repos." fades in/out
// - Frames 45-80: "Stop forgetting what you shipped." fades in/out
// - Frames 75-95: "DevLog" pops in large, green, spring
// - Frames 85-100: "Open-source. Local-first." appears below
// - Frames 95-115: Cursor blinks
// - Frames 115-120: Everything fades to black

export const Scene5Final: React.FC = () => {
  const frame = useCurrentFrame();
  const { fps } = useVideoConfig();

  // Final fade to black
  const fadeOut = interpolate(frame, [115, 120], [1, 0], {
    extrapolateLeft: "clamp",
    extrapolateRight: "clamp",
  });

  return (
    <AbsoluteFill
      style={{
        backgroundColor: colors.black,
        justifyContent: "center",
        alignItems: "center",
        opacity: fadeOut,
      }}
    >
      {/* "You ship across too many repos." */}
      <Sequence from={10} durationInFrames={40} layout="none">
        <TaglineText text="You ship across too many repos." />
      </Sequence>

      {/* "Stop forgetting what you shipped." */}
      <Sequence from={45} durationInFrames={35} layout="none">
        <TaglineText text="Stop forgetting what you shipped." />
      </Sequence>

      {/* DevLog logo */}
      {frame >= 75 && <DevLogLogo frame={frame} fps={fps} />}

      {/* Subtitle */}
      {frame >= 85 && (
        <div
          style={{
            position: "absolute",
            top: "58%",
            opacity: interpolate(frame, [85, 95], [0, 1], {
              extrapolateLeft: "clamp",
              extrapolateRight: "clamp",
            }),
          }}
        >
          <span
            style={{
              fontFamily: fontFamily.display,
              fontSize: 24,
              color: colors.secondary,
            }}
          >
            Open-source. Local-first.
          </span>
        </div>
      )}

      {/* Cursor */}
      {frame >= 95 && frame < 115 && (
        <div style={{ position: "absolute", top: "66%" }}>
          <CursorBlink color={colors.green} fontSize={28} />
        </div>
      )}
    </AbsoluteFill>
  );
};

const TaglineText: React.FC<{ text: string }> = ({ text }) => {
  const frame = useCurrentFrame();
  const { durationInFrames } = useVideoConfig();

  const opacity = interpolate(
    frame,
    [0, 10, durationInFrames - 10, durationInFrames],
    [0, 1, 1, 0],
    { extrapolateLeft: "clamp", extrapolateRight: "clamp" },
  );

  return (
    <AbsoluteFill
      style={{
        justifyContent: "center",
        alignItems: "center",
      }}
    >
      <div
        style={{
          fontFamily: fontFamily.display,
          fontSize: 48,
          color: colors.text,
          opacity,
          textAlign: "center",
        }}
      >
        {text}
      </div>
    </AbsoluteFill>
  );
};

const DevLogLogo: React.FC<{ frame: number; fps: number }> = ({
  frame,
  fps,
}) => {
  const springVal = spring({
    frame: frame - 75,
    fps,
    config: { damping: 20, stiffness: 200 },
  });

  const scale = interpolate(springVal, [0, 1], [0.5, 1]);
  const opacity = springVal;

  return (
    <div
      style={{
        position: "absolute",
        top: "42%",
        transform: `translateY(-50%) scale(${scale})`,
        opacity,
      }}
    >
      <span
        style={{
          fontFamily: fontFamily.display,
          fontSize: 96,
          fontWeight: 700,
          color: colors.green,
        }}
      >
        DevLog
      </span>
    </div>
  );
};

