import React from "react";
import { Sequence, staticFile, interpolate, useVideoConfig } from "remotion";
import { Audio } from "@remotion/media";

// Audio orchestrator — all sounds managed here
// Uses Sequence for timing and volume callbacks for fades
// Scene 5 is intentionally silent

export const AudioLayer: React.FC = () => {
  const { fps } = useVideoConfig();

  return (
    <>
      {/* Scene 1: Slack notification at frame 5 */}
      <Sequence from={5} durationInFrames={30} layout="none">
        <Audio src={staticFile("sounds/slack-notification.mp3")} volume={0.7} />
      </Sequence>

      {/* Scene 1-2: Ambient hum fades in at frame 10, runs through scene 2 */}
      <Sequence from={10} durationInFrames={380} layout="none">
        <Audio
          src={staticFile("sounds/ambient-hum.mp3")}
          loop
          volume={(f) =>
            interpolate(f, [0, 30, 350, 380], [0, 0.15, 0.15, 0], {
              extrapolateLeft: "clamp",
              extrapolateRight: "clamp",
            })
          }
        />
      </Sequence>

      {/* Scene 2: Fast typing during chaos (frames 150-240 global = 0-90 in scene 2) */}
      <Sequence from={155} durationInFrames={80} layout="none">
        <Audio
          src={staticFile("sounds/typing-fast.mp3")}
          volume={(f) =>
            interpolate(f, [0, 10, 70, 80], [0, 0.3, 0.3, 0], {
              extrapolateLeft: "clamp",
              extrapolateRight: "clamp",
            })
          }
        />
      </Sequence>

      {/* Scene 2: Notification stack during chaos montage */}
      <Sequence from={225} durationInFrames={60} layout="none">
        <Audio
          src={staticFile("sounds/notification-stack.mp3")}
          volume={0.4}
        />
      </Sequence>

      {/* Scene 3: Soft typing during terminal demo */}
      <Sequence from={420} durationInFrames={280} layout="none">
        <Audio
          src={staticFile("sounds/typing-soft.mp3")}
          loop
          volume={(f) =>
            interpolate(f, [0, 15, 260, 280], [0, 0.25, 0.25, 0], {
              extrapolateLeft: "clamp",
              extrapolateRight: "clamp",
            })
          }
        />
      </Sequence>

      {/* Scene 4: Soft typing for Slack reply */}
      <Sequence from={735} durationInFrames={90} layout="none">
        <Audio
          src={staticFile("sounds/typing-soft.mp3")}
          volume={0.2}
        />
      </Sequence>

      {/* Scene 4: Send message whoosh */}
      <Sequence from={810} durationInFrames={30} layout="none">
        <Audio src={staticFile("sounds/send-message.mp3")} volume={0.5} />
      </Sequence>

      {/* Scene 5: Silent — no audio */}
    </>
  );
};

