import React from "react";
import {
  AbsoluteFill,
  useCurrentFrame,
  useVideoConfig,
  interpolate,
  spring,
} from "remotion";
import { colors } from "../design/colors";
import { SlackUI } from "../components/SlackUI";
import { SlackMessage } from "../components/SlackMessage";
import { TypingIndicator } from "../components/TypingIndicator";

// Scene 1: 150 frames (0-5s)
// - Frames 0-20: Black → Slack UI fade-in
// - Frames 25-70: Manager message typewriters in
// - Frames 70-120: Typing indicator blinks
// - Frames 100-145: Slow zoom into message
// - Frames 145-150: Hard cut to black

export const Scene1Slack: React.FC = () => {
  const frame = useCurrentFrame();
  const { fps } = useVideoConfig();

  // Fade in
  const fadeIn = interpolate(frame, [0, 20], [0, 1], {
    extrapolateLeft: "clamp",
    extrapolateRight: "clamp",
  });

  // Scale entrance
  const scaleEntrance = interpolate(frame, [0, 20], [0.97, 1.0], {
    extrapolateLeft: "clamp",
    extrapolateRight: "clamp",
  });

  // Slow zoom toward end
  const zoomIn = interpolate(frame, [100, 145], [1.0, 1.08], {
    extrapolateLeft: "clamp",
    extrapolateRight: "clamp",
  });

  const scale = frame < 100 ? scaleEntrance : scaleEntrance * zoomIn;

  // Hard cut to black at end
  const cutToBlack = frame >= 145 ? 0 : 1;

  const opacity = fadeIn * cutToBlack;

  return (
    <AbsoluteFill
      style={{
        backgroundColor: colors.black,
        justifyContent: "center",
        alignItems: "center",
      }}
    >
      <div
        style={{
          opacity,
          transform: `scale(${scale})`,
        }}
      >
        <SlackUI>
          <SlackMessage
            name="Sarah Chen"
            avatar="SC"
            text="Performance review today. Let's go through what you've been doing for the last 3 months."
            startFrame={25}
            charsPerFrame={0.7}
            avatarColor={colors.slackYellow}
          />
          <TypingIndicator startFrame={70} endFrame={120} />
        </SlackUI>
      </div>
    </AbsoluteFill>
  );
};

