import React from "react";
import {
  AbsoluteFill,
  Sequence,
  useCurrentFrame,
  interpolate,
} from "remotion";
import { colors } from "../design/colors";
import { FlashText } from "../components/FlashText";
import { GitLogScroll } from "../components/GitLogScroll";
import { fontFamily } from "../design/fonts";

// Scene 2: 240 frames (5-13s)
// - Frames 0-10: Black
// - Frames 10-75: Stats flash one at a time
// - Frames 75-140: Rapid montage — git log + flash words
// - Frames 140-155: Hard cut to black
// - Frames 155-235: "Stop scrolling through git logs." fade in/out

export const Scene2Chaos: React.FC = () => {
  const frame = useCurrentFrame();

  return (
    <AbsoluteFill style={{ backgroundColor: colors.black }}>
      {/* Stats flash — hard cuts */}
      <Sequence from={10} durationInFrames={16} layout="none">
        <FlashText text="12,000 lines of code." fontSize={80} useSpring />
      </Sequence>
      <Sequence from={26} durationInFrames={16} layout="none">
        <FlashText text="5 repositories." fontSize={80} useSpring />
      </Sequence>
      <Sequence from={42} durationInFrames={16} layout="none">
        <FlashText text="12 branches." fontSize={80} useSpring />
      </Sequence>
      <Sequence from={58} durationInFrames={16} layout="none">
        <FlashText text="3 teams." fontSize={80} useSpring />
      </Sequence>

      {/* Rapid montage */}
      <Sequence from={75} durationInFrames={65} layout="none">
        <ChaosOverlay />
      </Sequence>

      {/* Stop scrolling message */}
      <Sequence from={155} durationInFrames={80} layout="none">
        <FlashText
          text="Stop scrolling through git logs."
          fontSize={56}
          fadeIn
          fadeOut
          fadeInDuration={15}
          fadeOutDuration={15}
        />
      </Sequence>
    </AbsoluteFill>
  );
};

const ChaosOverlay: React.FC = () => {
  const frame = useCurrentFrame();

  const overallOpacity = interpolate(frame, [0, 5, 60, 65], [0, 1, 1, 0], {
    extrapolateLeft: "clamp",
    extrapolateRight: "clamp",
  });

  return (
    <AbsoluteFill style={{ opacity: overallOpacity }}>
      {/* Git log scrolling background */}
      <AbsoluteFill
        style={{
          opacity: 0.3,
          padding: "60px 100px",
        }}
      >
        <GitLogScroll speed={3} />
      </AbsoluteFill>

      {/* Flash words on top */}
      <Sequence from={5} durationInFrames={18} layout="none">
        <FlashText text="Context switches." fontSize={64} useSpring />
      </Sequence>
      <Sequence from={23} durationInFrames={18} layout="none">
        <FlashText text="Standups." fontSize={64} useSpring />
      </Sequence>
      <Sequence from={41} durationInFrames={18} layout="none">
        <FlashText text="Hotfixes." fontSize={64} useSpring />
      </Sequence>

      {/* Overlapping branch names scattered */}
      <AbsoluteFill style={{ opacity: 0.15 }}>
        {[
          { text: "feature/auth", x: 200, y: 150, rot: -5 },
          { text: "fix/payments", x: 900, y: 300, rot: 3 },
          { text: "main", x: 400, y: 600, rot: -2 },
          { text: "staging", x: 1100, y: 500, rot: 7 },
          { text: "feature/api-v2", x: 600, y: 200, rot: -3 },
          { text: "hotfix/prod-crash", x: 300, y: 450, rot: 4 },
        ].map((item, i) => (
          <div
            key={i}
            style={{
              position: "absolute",
              left: item.x,
              top: item.y,
              transform: `rotate(${item.rot}deg)`,
              fontFamily: fontFamily.mono,
              fontSize: 18,
              color: colors.green,
            }}
          >
            {item.text}
          </div>
        ))}
      </AbsoluteFill>
    </AbsoluteFill>
  );
};

