import React from "react";
import {
  useCurrentFrame,
  useVideoConfig,
  spring,
  interpolate,
} from "remotion";
import { colors } from "../design/colors";
import { fontFamily } from "../design/fonts";

const WORKLOG_LINES = [
  { type: "h1", text: "Work Summary — Last 90 Days" },
  { type: "h2", text: "Payments Service" },
  { type: "li", text: "Shipped Stripe integration with retry logic" },
  { type: "li", text: "Fixed race condition in concurrent checkouts" },
  { type: "li", text: "Added webhook signature verification" },
  { type: "h2", text: "Authentication" },
  { type: "li", text: "Implemented OAuth2 + SSO login flow" },
  { type: "li", text: "Added rate limiting to auth endpoints" },
  { type: "h2", text: "Infrastructure" },
  { type: "li", text: "Resolved N+1 queries (40% latency reduction)" },
  { type: "li", text: "Set up monitoring dashboard + alerts" },
] as const;

export const MarkdownPreview: React.FC<{
  startFrame?: number;
}> = ({ startFrame = 0 }) => {
  const frame = useCurrentFrame();
  const { fps } = useVideoConfig();

  const slideIn = spring({
    frame: frame - startFrame,
    fps,
    config: { damping: 200 },
  });

  const translateX = interpolate(slideIn, [0, 1], [400, 0]);
  const opacity = slideIn;

  return (
    <div
      style={{
        backgroundColor: "#111318",
        borderRadius: 10,
        border: "1px solid rgba(255,255,255,0.08)",
        padding: "28px 32px",
        width: 560,
        opacity,
        transform: `translateX(${translateX}px)`,
        display: "flex",
        flexDirection: "column",
        gap: 8,
      }}
    >
      {WORKLOG_LINES.map((line, i) => {
        const lineDelay = startFrame + 5 + i * 3;
        const lineOpacity = interpolate(
          frame,
          [lineDelay, lineDelay + 6],
          [0, 1],
          { extrapolateLeft: "clamp", extrapolateRight: "clamp" },
        );

        if (line.type === "h1") {
          return (
            <div
              key={i}
              style={{
                fontFamily: fontFamily.display,
                fontSize: 20,
                fontWeight: 700,
                color: colors.green,
                opacity: lineOpacity,
                marginBottom: 8,
              }}
            >
              {line.text}
            </div>
          );
        }

        if (line.type === "h2") {
          return (
            <div
              key={i}
              style={{
                fontFamily: fontFamily.display,
                fontSize: 16,
                fontWeight: 700,
                color: colors.text,
                opacity: lineOpacity,
                marginTop: 8,
              }}
            >
              {line.text}
            </div>
          );
        }

        return (
          <div
            key={i}
            style={{
              fontFamily: fontFamily.display,
              fontSize: 14,
              color: colors.secondary,
              opacity: lineOpacity,
              paddingLeft: 16,
            }}
          >
            {"• "}
            {line.text}
          </div>
        );
      })}
    </div>
  );
};

