const WorklogMessage: React.FC<{ frame: number }> = ({ frame }) => {
  const entranceOpacity = interpolate(frame, [10, 18], [0, 1], {
    extrapolateLeft: "clamp",
    extrapolateRight: "clamp",
  });

  const lines = WORKLOG_TEXT.split("\n");

  return (
    <div
      style={{
        display: "flex",
        gap: 12,
        alignItems: "flex-start",
        opacity: entranceOpacity,
