          style={{
            display: "flex",
            flexDirection: "column",
            gap: 1,
            maxWidth: 700,
          }}
        >
          {lines.map((line, i) => {
            const lineFrame = 10 + i * 2;
            const lineOpacity = interpolate(
              frame,
              [lineFrame, lineFrame + 4],
              [0, 1],
              { extrapolateLeft: "clamp", extrapolateRight: "clamp" },
            );

            if (line.startsWith("## ")) {
              return (
                <div
                  key={i}
                  style={{
                    fontFamily: fontFamily.display,
                    fontSize: 15,
                    fontWeight: 700,
                    color: colors.green,
                    opacity: lineOpacity,
                    marginBottom: 4,
                  }}
                >
                  {line.replace("## ", "")}
                </div>
              );
            }
            if (line.startsWith("### ")) {
              return (
                <div
                  key={i}
                  style={{
                    fontFamily: fontFamily.display,
                    fontSize: 13,
                    fontWeight: 700,
                    color: colors.slackText,
                    opacity: lineOpacity,
                    marginTop: 4,
                  }}
                >
                  {line.replace("### ", "")}
                </div>
              );
            }
