import React from "react";
import {
  AbsoluteFill,
  Sequence,
  useCurrentFrame,
  useVideoConfig,
  spring,
  interpolate,
} from "remotion";
import { colors } from "../design/colors";
import { fontFamily } from "../design/fonts";
import { CursorBlink } from "../components/CursorBlink";

// Scene 5: 270 frames (9s)
// Phase 1 (0-170): Taglines stack up one by one, all visible together, pause
// Phase 2 (170-180): Fade out taglines
// Phase 3 (180-220): "DevLog" pops in large, green, snappy spring
// Phase 4 (200-230): "Open-source. Local-first." appears below
// Phase 5 (220-255): Cursor blinks
// Phase 6 (255-270): Everything fades to black

const TAGLINES = [
  "Too many repos.",
  "Too many branches.",
  "Too many pull requests.",
  "Stop forgetting what you shipped.",
];

const TAGLINE_INTERVAL = 28;

export const Scene5Final: React.FC = () => {
  const frame = useCurrentFrame();
  const { fps } = useVideoConfig();

  // Final fade to black
  const fadeOut = interpolate(frame, [255, 270], [1, 0], {
    extrapolateLeft: "clamp",
    extrapolateRight: "clamp",
  });

  return (
    <AbsoluteFill
      style={{
        backgroundColor: colors.black,
        justifyContent: "center",
        alignItems: "center",
        opacity: fadeOut,
      }}
    >
      {/* Phase 1: Stacking taglines */}
      <Sequence from={5} durationInFrames={175} layout="none">
        <StackingTaglines />
      </Sequence>

      {/* Phase 3: DevLog logo */}
      {frame >= 180 && <DevLogLogo frame={frame} fps={fps} />}

      {/* Phase 4: Subtitle */}
      {frame >= 200 && (
        <div
          style={{
            position: "absolute",
            top: "58%",
            opacity: interpolate(frame, [200, 215], [0, 1], {
              extrapolateLeft: "clamp",
              extrapolateRight: "clamp",
            }),
          }}
        >
          <span
            style={{
              fontFamily: fontFamily.display,
              fontSize: 24,
              color: colors.secondary,
            }}
          >
            Open-source. Local-first.
          </span>
        </div>
      )}

      {/* Phase 5: Cursor */}
      {frame >= 220 && frame < 255 && (
        <div style={{ position: "absolute", top: "66%" }}>
          <CursorBlink color={colors.green} fontSize={28} />
        </div>
      )}
    </AbsoluteFill>
  );
};

const StackingTaglines: React.FC = () => {
  const frame = useCurrentFrame();
  const { fps, durationInFrames } = useVideoConfig();

  // Fade out all taglines before DevLog logo
  const allFadeOut = interpolate(
    frame,
    [durationInFrames - 15, durationInFrames],
    [1, 0],
    { extrapolateLeft: "clamp", extrapolateRight: "clamp" },
  );

  return (
    <AbsoluteFill
      style={{
        justifyContent: "center",
        alignItems: "center",
        opacity: allFadeOut,
      }}
    >
      <div
        style={{
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          gap: 16,
        }}
      >
        {TAGLINES.map((tagline, i) => {
          const enterFrame = i * TAGLINE_INTERVAL;
          const isVisible = frame >= enterFrame;
          const isLast = i === TAGLINES.length - 1;

          if (!isVisible) return null;

          const localFrame = frame - enterFrame;

          const springVal = spring({
            frame: localFrame,
            fps,
            config: { damping: 20, stiffness: 200 },
          });

          const opacity = springVal;
          const translateY = interpolate(springVal, [0, 1], [25, 0]);
          const scale = interpolate(springVal, [0, 1], [0.85, 1]);

          return (
            <div
              key={i}
              style={{
                fontFamily: fontFamily.display,
                fontSize: isLast ? 52 : 48,
                fontWeight: 700,
                color: isLast ? colors.green : colors.text,
                opacity,
                transform: `translateY(${translateY}px) scale(${scale})`,
                textAlign: "center",
              }}
            >
              {tagline}
            </div>
          );
        })}
      </div>
    </AbsoluteFill>
  );
};

const DevLogLogo: React.FC<{ frame: number; fps: number }> = ({
  frame,
  fps,
}) => {
  const springVal = spring({
    frame: frame - 180,
    fps,
    config: { damping: 20, stiffness: 200 },
  });

  const scale = interpolate(springVal, [0, 1], [0.5, 1]);
  const opacity = springVal;

  return (
    <div
      style={{
        position: "absolute",
        top: "42%",
        transform: `translateY(-50%) scale(${scale})`,
        opacity,
      }}
    >
      <span
        style={{
          fontFamily: fontFamily.display,
          fontSize: 96,
          fontWeight: 700,
          color: colors.green,
        }}
      >
        DevLog
      </span>
    </div>
  );
};

