import React from "react";
import {
  AbsoluteFill,
  useCurrentFrame,
  useVideoConfig,
  interpolate,
} from "remotion";
import { colors } from "../design/colors";
import { SlackUI } from "../components/SlackUI";
import { SlackMessage } from "../components/SlackMessage";
import { TypingIndicator } from "../components/TypingIndicator";

// Scene 1: 210 frames (7s)
// - Frames 0-20: Black → Slack UI fade-in (zoomed in)
// - Frames 25-90: Manager message typewriters in (bigger font)
// - Frames 90-160: Hold on message so user can read it
// - Frames 130-170: Typing indicator blinks (no reply)
// - Frames 160-200: Slow zoom into message
// - Frames 200-210: Hard cut to black

export const Scene1Slack: React.FC = () => {
  const frame = useCurrentFrame();

  // Fade in
  const fadeIn = interpolate(frame, [0, 20], [0, 1], {
    extrapolateLeft: "clamp",
    extrapolateRight: "clamp",
  });

  // Start zoomed in more (1.25 base scale) with subtle entrance
  const scaleEntrance = interpolate(frame, [0, 20], [1.2, 1.25], {
    extrapolateLeft: "clamp",
    extrapolateRight: "clamp",
  });

  // Slow zoom toward end
  const zoomIn = interpolate(frame, [160, 200], [1.0, 1.1], {
    extrapolateLeft: "clamp",
    extrapolateRight: "clamp",
  });

  const scale = scaleEntrance * zoomIn;

  // Hard cut to black at end
  const cutToBlack = frame >= 200 ? 0 : 1;

  const opacity = fadeIn * cutToBlack;

  return (
    <AbsoluteFill
      style={{
        backgroundColor: colors.black,
        justifyContent: "center",
        alignItems: "center",
      }}
    >
      <div
        style={{
          opacity,
