import React from "react";
import {
  AbsoluteFill,
  Sequence,
  useCurrentFrame,
  useVideoConfig,
  interpolate,
  spring,
} from "remotion";
import { colors } from "../design/colors";
import { fontFamily } from "../design/fonts";
import { GitLogScroll } from "../components/GitLogScroll";

// Scene 2: 360 frames (12s)
// Phase 1 (0-200): Stats stack up one by one, all visible together, then pause
// Phase 2 (200-210): Hard cut to black
// Phase 3 (210-290): Flash words: "Context switches." "Standups." "Hotfixes."
// Phase 4 (290-360): "Stop scrolling through git logs." fade in/out
// Git log + branches scroll at the bottom throughout

const STATS = [
  "12,000 lines of code.",
  "5 repositories.",
  "12 branches.",
  "3 teams.",
];

const STAT_ENTER_INTERVAL = 30; // frames between each stat appearing
const STAT_SETTLE_FRAME = STATS.length * STAT_ENTER_INTERVAL; // frame when all are visible

export const Scene2Chaos: React.FC = () => {
  const frame = useCurrentFrame();

  return (
    <AbsoluteFill style={{ backgroundColor: colors.black }}>
      {/* Phase 1: Stacking stats */}
      <Sequence from={10} durationInFrames={190} layout="none">
        <StackingStats />
      </Sequence>

      {/* Phase 3: Flash words */}
      <Sequence from={210} durationInFrames={25} layout="none">
        <FlashWord text="Context switches." />
      </Sequence>
      <Sequence from={235} durationInFrames={25} layout="none">
        <FlashWord text="Standups." />
      </Sequence>
      <Sequence from={260} durationInFrames={25} layout="none">
        <FlashWord text="Hotfixes." />
      </Sequence>

      {/* Phase 4: "Stop scrolling through git logs." */}
      <Sequence from={290} durationInFrames={70} layout="none">
        <StopScrolling />
      </Sequence>

      {/* Git log + branches at the bottom, throughout */}
      <Sequence from={10} durationInFrames={280} layout="none">
        <BottomChaos />
      </Sequence>
    </AbsoluteFill>
  );
};

const StackingStats: React.FC = () => {
  const frame = useCurrentFrame();
  const { fps } = useVideoConfig();

  return (
    <AbsoluteFill
      style={{
        justifyContent: "center",
        alignItems: "center",
      }}
    >
      <div
        style={{
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          gap: 20,
        }}
      >
        {STATS.map((stat, i) => {
          const enterFrame = i * STAT_ENTER_INTERVAL;
          const isVisible = frame >= enterFrame;

          if (!isVisible) return null;

          const localFrame = frame - enterFrame;

          // Spring entrance
          const springVal = spring({
            frame: localFrame,
            fps,
            config: { damping: 20, stiffness: 200 },
          });

          const opacity = springVal;
          const translateY = interpolate(springVal, [0, 1], [30, 0]);
          const scale = interpolate(springVal, [0, 1], [0.85, 1]);

          return (
            <div
              key={i}
              style={{
                fontFamily: fontFamily.display,
                fontSize: 64,
                fontWeight: 700,
                color: colors.text,
                opacity,
                transform: `translateY(${translateY}px) scale(${scale})`,
                textAlign: "center",
              }}
            >
              {stat}
            </div>
          );
        })}
      </div>
    </AbsoluteFill>
  );
};

const FlashWord: React.FC<{ text: string }> = ({ text }) => {
  const frame = useCurrentFrame();
  const { fps, durationInFrames } = useVideoConfig();

  const springVal = spring({
    frame,
    fps,
    config: { damping: 20, stiffness: 200 },
  });

  const scale = interpolate(springVal, [0, 1], [0.8, 1]);
  const opacity = interpolate(
    frame,
    [0, 5, durationInFrames - 5, durationInFrames],
    [0, 1, 1, 0],
    { extrapolateLeft: "clamp", extrapolateRight: "clamp" },
  );

  return (
    <AbsoluteFill
      style={{
        justifyContent: "center",
        alignItems: "center",
      }}
    >
      <div
        style={{
          fontFamily: fontFamily.display,
          fontSize: 72,
          fontWeight: 700,
          color: colors.text,
          opacity,
          transform: `scale(${scale})`,
        }}
      >
        {text}
      </div>
    </AbsoluteFill>
  );
};

const StopScrolling: React.FC = () => {
  const frame = useCurrentFrame();
  const { durationInFrames } = useVideoConfig();

  const opacity = interpolate(
    frame,
    [0, 15, durationInFrames - 15, durationInFrames],
    [0, 1, 1, 0],
    { extrapolateLeft: "clamp", extrapolateRight: "clamp" },
  );

  return (
    <AbsoluteFill
      style={{
        justifyContent: "center",
        alignItems: "center",
