      <div
        style={{
          fontFamily: fontFamily.display,
          fontSize: 56,
          fontWeight: 700,
          color: colors.text,
          opacity,
          textAlign: "center",
          padding: "0 80px",
        }}
      >
        Stop scrolling through git logs.
      </div>
    </AbsoluteFill>
  );
};

const BottomChaos: React.FC = () => {
  const frame = useCurrentFrame();
  const { durationInFrames } = useVideoConfig();

  const opacity = interpolate(
