        }}
      >
        <GitLogScroll speed={3} />
      </div>

      {/* Branch names scattered at the bottom */}
      {[
        { text: "feature/auth", x: 100, y: 720, rot: -4 },
        { text: "fix/payments-race", x: 500, y: 780, rot: 3 },
        { text: "main", x: 900, y: 740, rot: -2 },
        { text: "staging", x: 1300, y: 770, rot: 5 },
        { text: "feature/api-v2", x: 300, y: 850, rot: -3 },
        { text: "hotfix/prod-crash", x: 750, y: 830, rot: 4 },
        { text: "release/v2.1", x: 1100, y: 860, rot: -1 },
        { text: "feature/sso", x: 1500, y: 750, rot: 2 },
      ].map((item, i) => {
        const itemOpacity = interpolate(
          frame,
          [i * 8, i * 8 + 15],
          [0, 0.6],
          { extrapolateLeft: "clamp", extrapolateRight: "clamp" },
        );
        return (
          <div
            key={i}
            style={{
              position: "absolute",
              left: item.x,
              top: item.y,
              transform: `rotate(${item.rot}deg)`,
              fontFamily: fontFamily.mono,
              fontSize: 16,
              color: colors.green,
              opacity: itemOpacity,
            }}
          >
