              return (
                <div
                  key={i}
                  style={{
                    fontFamily: fontFamily.display,
                    fontSize: 15,
                    fontWeight: 700,
                    color: colors.green,
                    opacity: lineOpacity,
                    marginBottom: 4,
                  }}
                >
                  {line.replace("## ", "")}
                </div>
              );
            }
            if (line.startsWith("### ")) {
              return (
                <div
                  key={i}
                  style={{
                    fontFamily: fontFamily.display,
                    fontSize: 13,
                    fontWeight: 700,
                    color: colors.slackText,
                    opacity: lineOpacity,
                    marginTop: 4,
                  }}
                >
                  {line.replace("### ", "")}
                </div>
              );
            }
            if (line.startsWith("•")) {
              return (
                <div
                  key={i}
                  style={{
                    fontFamily: fontFamily.display,
                    fontSize: 12,
                    color: colors.secondary,
                    opacity: lineOpacity,
                    paddingLeft: 12,
                    lineHeight: 1.5,
                  }}
                >
                  {line}
                </div>
              );
            }
            if (line.trim() === "")
              return <div key={i} style={{ height: 4 }} />;
            return null;
          })}
        </div>
      </div>
    </div>
  );
};

const EmojiReaction: React.FC<{
  emoji: string;
  frame: number;
  startFrame: number;
}> = ({ emoji, frame, startFrame }) => {
  const elapsed = frame - startFrame;
  const opacity = interpolate(elapsed, [0, 8], [0, 1], {
    extrapolateLeft: "clamp",
    extrapolateRight: "clamp",
  });
  const scale = interpolate(elapsed, [0, 6], [0.3, 1], {
    extrapolateLeft: "clamp",
    extrapolateRight: "clamp",
  });

  return (
    <div
      style={{
        marginLeft: 52,
        opacity,
        transform: `scale(${scale})`,
        transformOrigin: "left center",
        fontSize: 28,
        padding: "2px 0",
      }}
    >
      {emoji}
    </div>
  );
};

const Confetti: React.FC<{ frame: number; startFrame: number }> = ({
  frame,
  startFrame,
}) => {
  const elapsed = frame - startFrame;

  // Fade out confetti after a while
  const fadeOut = interpolate(elapsed, [60, 100], [1, 0], {
    extrapolateLeft: "clamp",
    extrapolateRight: "clamp",
  });

  return (
    <AbsoluteFill
      style={{
        pointerEvents: "none",
        opacity: fadeOut,
        overflow: "hidden",
      }}
    >
      {confettiParticles.map((p, i) => {
        const t = Math.max(0, elapsed - p.delay);
        if (t <= 0) return null;

        const y = -50 + t * p.speed * 3;
        const x = p.x + Math.sin(t * 0.1 + i) * p.wobble * 20;
        const rotation = p.rotation + t * p.rotSpeed;

        // Particle opacity
        const pOpacity = interpolate(t, [0, 5], [0, 1], {
          extrapolateLeft: "clamp",
          extrapolateRight: "clamp",
        });

        if (y > 1200) return null;

        const shapeStyle: React.CSSProperties = {
          position: "absolute",
          left: x,
          top: y,
          width: p.size,
          height: p.shape === 1 ? p.size : p.size * 0.6,
          backgroundColor: p.color,
          borderRadius: p.shape === 1 ? "50%" : p.shape === 2 ? "0" : "2px",
          transform: `rotate(${rotation}deg)`,
          opacity: pOpacity,
        };

        return <div key={i} style={shapeStyle} />;
      })}
    </AbsoluteFill>
  );
};

