import React from "react";
import {
  AbsoluteFill,
  useCurrentFrame,
  useVideoConfig,
  interpolate,
  spring,
} from "remotion";
import { colors } from "../design/colors";
import { fontFamily } from "../design/fonts";
import { SlackUI } from "../components/SlackUI";
import { SlackMessage } from "../components/SlackMessage";
import { TypingIndicator } from "../components/TypingIndicator";

// Scene 4: 480 frames (16s)
// - Frames 0-5: Instant cut to Slack UI with worklog pasted
// - Frames 5-10: Worklog message appears
// - Frames 10-70: User reads worklog (2s hold)
// - Frames 70-130: "You" reply typewriters in (the full summary)
// - Frame 130: Message finishes
// - Frames 130-190: 2s wait
// - Frames 190-240: Typing indicator from manager
// - Frames 240-250: Thumbs up reaction
// - Frames 260-350: Manager message: "Damn you've been shipping a lot, time for a promotion!"
// - Frames 360-380: Celebration emoji reaction from you
// - Frames 380-480: Hold on messages

const WORKLOG_TEXT = `## Work Summary — Last 90 Days

### Payments Service
• Shipped Stripe integration with retry logic
• Fixed race condition in concurrent checkouts
• Added webhook signature verification

### Authentication
• Implemented OAuth2 + SSO login flow
• Added rate limiting to auth endpoints

### Infrastructure
• Resolved N+1 queries (40% latency reduction)
• Set up monitoring dashboard + alerts`;

export const Scene4Reversal: React.FC = () => {
  const frame = useCurrentFrame();

  const opacity = interpolate(frame, [0, 5], [0, 1], {
    extrapolateLeft: "clamp",
    extrapolateRight: "clamp",
  });

  return (
    <AbsoluteFill
      style={{
        backgroundColor: colors.black,
        justifyContent: "center",
        alignItems: "center",
      }}
    >
      <div style={{ opacity }}>
        <SlackUI>
          {/* Original manager message (instant) */}
          <SlackMessage
            name="Sarah Chen"
            avatar="SC"
            text="Performance review today. Let's go through what you've been doing for the last 3 months."
            startFrame={0}
            charsPerFrame={100}
            avatarColor={colors.slackYellow}
          />

          {/* Your reply — worklog pasted */}
          {frame >= 10 && <WorklogMessage frame={frame} />}

          {/* Manager typing indicator */}
          <TypingIndicator startFrame={190} endFrame={240} />

          {/* Manager thumbs up */}
          {frame >= 240 && <EmojiReaction emoji="👍" frame={frame} startFrame={240} />}

          {/* Manager promotion message */}
          <SlackMessage
            name="Sarah Chen"
            avatar="SC"
            text="Damn you've been shipping a lot, time for a promotion!"
            startFrame={260}
            charsPerFrame={0.8}
            avatarColor={colors.slackYellow}
          />

          {/* Your celebration reaction */}
          {frame >= 360 && <EmojiReaction emoji="🎉" frame={frame} startFrame={360} />}
        </SlackUI>
      </div>
    </AbsoluteFill>
  );
};

const WorklogMessage: React.FC<{ frame: number }> = ({ frame }) => {
  const entranceOpacity = interpolate(frame, [10, 18], [0, 1], {
    extrapolateLeft: "clamp",
    extrapolateRight: "clamp",
  });

  const lines = WORKLOG_TEXT.split("\n");

  return (
    <div
      style={{
        display: "flex",
        gap: 12,
        alignItems: "flex-start",
        opacity: entranceOpacity,
      }}
    >
      {/* Avatar */}
      <div
        style={{
          width: 40,
          height: 40,
          borderRadius: 6,
          backgroundColor: colors.slackGreen,
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          fontFamily: fontFamily.display,
          fontSize: 18,
          fontWeight: 700,
          color: colors.white,
          flexShrink: 0,
        }}
      >
        Y
      </div>
      {/* Content */}
      <div style={{ display: "flex", flexDirection: "column", gap: 2 }}>
        <div style={{ display: "flex", alignItems: "baseline", gap: 8 }}>
          <span
            style={{
              color: colors.white,
              fontFamily: fontFamily.display,
              fontWeight: 700,
              fontSize: 15,
            }}
          >
            You
          </span>
          <span
            style={{
              color: colors.secondary,
              fontFamily: fontFamily.display,
              fontSize: 12,
            }}
          >
            10:33 AM
          </span>
        </div>
        <div
          style={{
            display: "flex",
            flexDirection: "column",
            gap: 1,
            maxWidth: 700,
          }}
        >
          {lines.map((line, i) => {
            const lineFrame = 10 + i * 2;
            const lineOpacity = interpolate(
              frame,
              [lineFrame, lineFrame + 4],
              [0, 1],
              { extrapolateLeft: "clamp", extrapolateRight: "clamp" },
            );

            if (line.startsWith("## ")) {
              return (
                <div
                  key={i}
                  style={{
                    fontFamily: fontFamily.display,
                    fontSize: 15,
                    fontWeight: 700,
                    color: colors.green,
                    opacity: lineOpacity,
                    marginBottom: 4,
                  }}
                >
                  {line.replace("## ", "")}
                </div>
              );
            }
            if (line.startsWith("### ")) {
              return (
                <div
                  key={i}
                  style={{
                    fontFamily: fontFamily.display,
                    fontSize: 13,
                    fontWeight: 700,
                    color: colors.slackText,
                    opacity: lineOpacity,
                    marginTop: 4,
                  }}
                >
                  {line.replace("### ", "")}
                </div>
              );
            }
            if (line.startsWith("•")) {
              return (
                <div
                  key={i}
                  style={{
                    fontFamily: fontFamily.display,
                    fontSize: 12,
                    color: colors.secondary,
                    opacity: lineOpacity,
                    paddingLeft: 12,
                    lineHeight: 1.5,
                  }}
                >
                  {line}
                </div>
              );
            }
            if (line.trim() === "") return <div key={i} style={{ height: 4 }} />;
            return null;
          })}
        </div>
      </div>
    </div>
  );
};

const EmojiReaction: React.FC<{
  emoji: string;
  frame: number;
  startFrame: number;
}> = ({ emoji, frame, startFrame }) => {
  const elapsed = frame - startFrame;
  const opacity = interpolate(elapsed, [0, 8], [0, 1], {
    extrapolateLeft: "clamp",
    extrapolateRight: "clamp",
  });
  const scale = interpolate(elapsed, [0, 6], [0.3, 1], {
    extrapolateLeft: "clamp",
    extrapolateRight: "clamp",
  });

  return (
    <div
      style={{
        marginLeft: 52,
        opacity,
        transform: `scale(${scale})`,
        transformOrigin: "left center",
        fontSize: 28,
        padding: "2px 0",
      }}
    >
      {emoji}
    </div>
  );
};

