import React from "react";
import {
  AbsoluteFill,
  Sequence,
  useCurrentFrame,
  useVideoConfig,
  spring,
  interpolate,
} from "remotion";
import { colors } from "../design/colors";
import { fontFamily } from "../design/fonts";

// Scene 5: 270 frames (9s)
// Phase 1 (0-170): Taglines stack up one by one, all visible together, pause
// Phase 2 (170-180): Fade out taglines
// Phase 3 (180-270): DevLog screen (~3s) — 5s total feel with fade
//   - 180: "DevLog" snappy spring
//   - 195: "Open-source. Local-first."
//   - 210: "by @ishaan812"
//   - 195-260: Pulsing green glow animation
//   - 255-270: Fade to black

const TAGLINES = [
  "Too many repos.",
  "Too many branches.",
  "Too many pull requests.",
  "Stop forgetting what you shipped.",
];

const TAGLINE_INTERVAL = 28;

export const Scene5Final: React.FC = () => {
  const frame = useCurrentFrame();
  const { fps } = useVideoConfig();

  // Final fade to black
  const fadeOut = interpolate(frame, [255, 270], [1, 0], {
    extrapolateLeft: "clamp",
    extrapolateRight: "clamp",
  });

  return (
    <AbsoluteFill
      style={{
        backgroundColor: colors.black,
        justifyContent: "center",
        alignItems: "center",
        opacity: fadeOut,
      }}
    >
      {/* Phase 1: Stacking taglines */}
      <Sequence from={5} durationInFrames={175} layout="none">
        <StackingTaglines />
      </Sequence>

      {/* Phase 3: DevLog screen */}
      {frame >= 180 && (
        <>
          {/* Green glow pulse behind the logo */}
          <GreenGlow frame={frame} fps={fps} />

          {/* DevLog logo */}
          <DevLogLogo frame={frame} fps={fps} />

          {/* Subtitle: Open-source. Local-first. */}
          <div
            style={{
              position: "absolute",
              top: "57%",
              opacity: interpolate(frame, [195, 208], [0, 1], {
                extrapolateLeft: "clamp",
                extrapolateRight: "clamp",
              }),
            }}
          >
            <span
              style={{
                fontFamily: fontFamily.display,
                fontSize: 24,
                color: colors.secondary,
              }}
            >
              Open-source. Local-first.
            </span>
          </div>

          {/* by @ishaan812 */}
          <div
            style={{
              position: "absolute",
              top: "63%",
              opacity: interpolate(frame, [210, 222], [0, 1], {
                extrapolateLeft: "clamp",
                extrapolateRight: "clamp",
              }),
            }}
          >
            <span
              style={{
                fontFamily: fontFamily.mono,
                fontSize: 18,
                color: colors.green,
              }}
            >
              by @ishaan812
            </span>
          </div>

          {/* Animated cursor / pulse indicator */}
          {frame >= 215 && frame < 255 && (
            <div style={{ position: "absolute", top: "70%" }}>
              <PulsingCursor frame={frame} />
            </div>
          )}
        </>
      )}
    </AbsoluteFill>
  );
};

const StackingTaglines: React.FC = () => {
  const frame = useCurrentFrame();
  const { fps, durationInFrames } = useVideoConfig();

  const allFadeOut = interpolate(
    frame,
    [durationInFrames - 15, durationInFrames],
    [1, 0],
    { extrapolateLeft: "clamp", extrapolateRight: "clamp" },
  );

  return (
    <AbsoluteFill
      style={{
        justifyContent: "center",
        alignItems: "center",
        opacity: allFadeOut,
      }}
    >
      <div
        style={{
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          gap: 16,
        }}
      >
        {TAGLINES.map((tagline, i) => {
          const enterFrame = i * TAGLINE_INTERVAL;
          if (frame < enterFrame) return null;

          const isLast = i === TAGLINES.length - 1;
          const localFrame = frame - enterFrame;

          const springVal = spring({
            frame: localFrame,
            fps,
            config: { damping: 20, stiffness: 200 },
          });

          const opacity = springVal;
          const translateY = interpolate(springVal, [0, 1], [25, 0]);
          const scale = interpolate(springVal, [0, 1], [0.85, 1]);

          return (
            <div
              key={i}
              style={{
                fontFamily: fontFamily.display,
                fontSize: isLast ? 52 : 48,
                fontWeight: 700,
                color: isLast ? colors.green : colors.text,
                opacity,
                transform: `translateY(${translateY}px) scale(${scale})`,
                textAlign: "center",
              }}
            >
              {tagline}
            </div>
          );
        })}
      </div>
    </AbsoluteFill>
  );
};

const DevLogLogo: React.FC<{ frame: number; fps: number }> = ({
  frame,
  fps,
}) => {
  const springVal = spring({
    frame: frame - 180,
    fps,
    config: { damping: 20, stiffness: 200 },
  });

  const scale = interpolate(springVal, [0, 1], [0.5, 1]);
  const opacity = springVal;

  return (
    <div
      style={{
        position: "absolute",
        top: "42%",
        transform: `translateY(-50%) scale(${scale})`,
        opacity,
      }}
    >
      <span
        style={{
          fontFamily: fontFamily.display,
          fontSize: 96,
          fontWeight: 700,
          color: colors.green,
        }}
      >
        DevLog
      </span>
    </div>
  );
};

// Radial green glow that pulses behind the logo
const GreenGlow: React.FC<{ frame: number; fps: number }> = ({
  frame,
  fps,
}) => {
  const entrance = spring({
    frame: frame - 180,
    fps,
    config: { damping: 200 },
  });

  const elapsed = frame - 180;
  // Pulse: oscillate between 0.15 and 0.35 opacity
  const pulsePhase = Math.sin(elapsed * 0.08) * 0.5 + 0.5;
  const glowOpacity = entrance * interpolate(pulsePhase, [0, 1], [0.12, 0.3]);

  // Subtle scale breathing
  const breathe = 1 + Math.sin(elapsed * 0.06) * 0.05;

  return (
    <div
      style={{
        position: "absolute",
        top: "50%",
        left: "50%",
        width: 600,
        height: 600,
        transform: `translate(-50%, -55%) scale(${breathe})`,
        borderRadius: "50%",
        background: `radial-gradient(circle, ${colors.green}40 0%, ${colors.green}10 40%, transparent 70%)`,
        opacity: glowOpacity,
        filter: "blur(40px)",
      }}
    />
  );
};

// More interesting pulsing green cursor/bar animation
const PulsingCursor: React.FC<{ frame: number }> = ({ frame }) => {
  const elapsed = frame - 215;

  // Glow intensity oscillates
  const glowIntensity = Math.sin(elapsed * 0.25) * 0.5 + 0.5;
  const barOpacity = interpolate(glowIntensity, [0, 1], [0.3, 1]);
  const barWidth = interpolate(glowIntensity, [0, 1], [20, 35]);
