import React from "react";
import { Sequence, staticFile, interpolate } from "remotion";
import { Audio } from "@remotion/media";

// Audio orchestrator — all sounds managed here
// Scene offsets:
//   Scene 1 starts at frame 0   (210 frames)
//   Scene 2 starts at frame 210 (360 frames)
//   Scene 3 starts at frame 570 (330 frames)
//   Scene 4 starts at frame 900 (480 frames)
//   Scene 5 starts at frame 1380 (270 frames) — silent

export const AudioLayer: React.FC = () => {
  return (
    <>
      {/* Scene 1: Slack notification at frame 5 */}
      <Sequence from={5} durationInFrames={30} layout="none">
        <Audio src={staticFile("sounds/slack-notification.mp3")} volume={0.7} />
      </Sequence>

      {/* Scene 1-2: Ambient hum fades in at frame 10, runs through chaos */}
      <Sequence from={10} durationInFrames={550} layout="none">
        <Audio
          src={staticFile("sounds/ambient-hum.mp3")}
          loop
          volume={(f) =>
            interpolate(f, [0, 30, 520, 550], [0, 0.15, 0.15, 0], {
              extrapolateLeft: "clamp",
              extrapolateRight: "clamp",
            })
          }
        />
      </Sequence>

      {/* Scene 2: Fast typing during stacking stats (scene 2 starts at 210) */}
      <Sequence from={220} durationInFrames={120} layout="none">
        <Audio
          src={staticFile("sounds/typing-fast.mp3")}
          volume={(f) =>
            interpolate(f, [0, 10, 100, 120], [0, 0.3, 0.3, 0], {
              extrapolateLeft: "clamp",
              extrapolateRight: "clamp",
            })
          }
        />
      </Sequence>

      {/* Scene 2: Notification stack during chaos */}
      <Sequence from={350} durationInFrames={60} layout="none">
        <Audio
          src={staticFile("sounds/notification-stack.mp3")}
          volume={0.4}
        />
      </Sequence>

      {/* Scene 3: Soft typing during terminal demo (scene 3 starts at 570) */}
      <Sequence from={590} durationInFrames={300} layout="none">
        <Audio
          src={staticFile("sounds/typing-soft.mp3")}
          loop
          volume={(f) =>
            interpolate(f, [0, 15, 280, 300], [0, 0.25, 0.25, 0], {
              extrapolateLeft: "clamp",
              extrapolateRight: "clamp",
            })
          }
        />
      </Sequence>

      {/* Scene 4: Soft typing for worklog paste (scene 4 starts at 900) */}
      <Sequence from={910} durationInFrames={120} layout="none">
        <Audio
          src={staticFile("sounds/typing-soft.mp3")}
          volume={0.2}
        />
      </Sequence>

      {/* Scene 4: Send message whoosh when worklog posts */}
      <Sequence from={950} durationInFrames={30} layout="none">
        <Audio src={staticFile("sounds/send-message.mp3")} volume={0.5} />
      </Sequence>

      {/* Scene 4: Slack notification for promotion message (frame 900+260) */}
      <Sequence from={1160} durationInFrames={30} layout="none">
        <Audio src={staticFile("sounds/slack-notification.mp3")} volume={0.6} />
      </Sequence>

      {/* Scene 5: Silent — no audio */}
    </>
  );
};

