  1→import React from "react";
  2→import {
  3→  AbsoluteFill,
  4→  Sequence,
  5→  useCurrentFrame,
  6→  useVideoConfig,
  7→  spring,
  8→  interpolate,
  9→} from "remotion";
 10→import { colors } from "../design/colors";
 11→import { fontFamily } from "../design/fonts";
 12→
 13→// Scene 5: 270 frames (9s)
 14→// Phase 1 (0-170): Taglines stack up one by one, all visible together, pause
 15→// Phase 2 (170-180): Fade out taglines
 16→// Phase 3 (180-270): DevLog screen (~3s) — 5s total feel with fade
 17→//   - 180: "DevLog" snappy spring
 18→//   - 195: "Open-source. Local-first."
 19→//   - 210: "by @ishaan812"
 20→//   - 195-260: Pulsing green glow animation
 21→//   - 255-270: Fade to black
 22→
 23→const TAGLINES = [
 24→  "Too many repos.",
 25→  "Too many branches.",
 26→  "Too many pull requests.",
 27→  "Stop forgetting what you shipped.",
 28→];
 29→
 30→const TAGLINE_INTERVAL = 28;
 31→
 32→export const Scene5Final: React.FC = () => {
 33→  const frame = useCurrentFrame();
 34→  const { fps } = useVideoConfig();
 35→
 36→  // Final fade to black
 37→  const fadeOut = interpolate(frame, [255, 270], [1, 0], {
 38→    extrapolateLeft: "clamp",
 39→    extrapolateRight: "clamp",
 40→  });
 41→
 42→  return (
 43→    <AbsoluteFill
 44→      style={{
 45→        backgroundColor: colors.black,
 46→        justifyContent: "center",
 47→        alignItems: "center",
 48→        opacity: fadeOut,
 49→      }}
 50→    >
 51→      {/* Phase 1: Stacking taglines */}
 52→      <Sequence from={5} durationInFrames={175} layout="none">
 53→        <StackingTaglines />
 54→      </Sequence>
 55→
 56→      {/* Phase 3: DevLog screen */}
 57→      {frame >= 180 && (
 58→        <>
 59→          {/* Green glow pulse behind the logo */}
 60→          <GreenGlow frame={frame} fps={fps} />
 61→
 62→          {/* DevLog logo */}
 63→          <DevLogLogo frame={frame} fps={fps} />
 64→
 65→          {/* Subtitle: Open-source. Local-first. */}
 66→          <div
 67→            style={{
 68→              position: "absolute",
 69→              top: "57%",
 70→              opacity: interpolate(frame, [195, 208], [0, 1], {
 71→                extrapolateLeft: "clamp",
 72→                extrapolateRight: "clamp",
 73→              }),
 74→            }}
 75→          >
 76→            <span
 77→              style={{
 78→                fontFamily: fontFamily.display,
 79→                fontSize: 24,
 80→                color: colors.secondary,
 81→              }}
 82→            >
 83→              Open-source. Local-first.
 84→            </span>
 85→          </div>
 86→
 87→          {/* by @ishaan812 */}
 88→          <div
 89→            style={{
 90→              position: "absolute",
 91→              top: "63%",
 92→              opacity: interpolate(frame, [210, 222], [0, 1], {
 93→                extrapolateLeft: "clamp",
 94→                extrapolateRight: "clamp",
 95→              }),
 96→            }}
 97→          >
 98→            <span
 99→              style={{
100→                fontFamily: fontFamily.mono,
101→                fontSize: 18,
102→                color: colors.green,
103→              }}
104→            >
105→              by @ishaan812
106→            </span>
107→          </div>
108→
109→          {/* Animated cursor / pulse indicator */}
110→          {frame >= 215 && frame < 255 && (
111→            <div style={{ position: "absolute", top: "70%" }}>
112→              <PulsingCursor frame={frame} />
113→            </div>
114→          )}
115→        </>
116→      )}
117→    </AbsoluteFill>
118→  );
119→};
120→
121→const StackingTaglines: React.FC = () => {
122→  const frame = useCurrentFrame();
123→  const { fps, durationInFrames } = useVideoConfig();
124→
125→  const allFadeOut = interpolate(
126→    frame,
127→    [durationInFrames - 15, durationInFrames],
128→    [1, 0],
129→    { extrapolateLeft: "clamp", extrapolateRight: "clamp" },
130→  );
131→
132→  return (
133→    <AbsoluteFill
134→      style={{
135→        justifyContent: "center",
136→        alignItems: "center",
137→        opacity: allFadeOut,
138→      }}
139→    >
140→      <div
141→        style={{
142→          display: "flex",
143→          flexDirection: "column",
144→          alignItems: "center",
145→          gap: 16,
146→        }}
147→      >
148→        {TAGLINES.map((tagline, i) => {
149→          const enterFrame = i * TAGLINE_INTERVAL;
150→          if (frame < enterFrame) return null;
151→
152→          const isLast = i === TAGLINES.length - 1;
153→          const localFrame = frame - enterFrame;
154→
155→          const springVal = spring({
156→            frame: localFrame,
157→            fps,
158→            config: { damping: 20, stiffness: 200 },
159→          });
160→
161→          const opacity = springVal;
162→          const translateY = interpolate(springVal, [0, 1], [25, 0]);
163→          const scale = interpolate(springVal, [0, 1], [0.85, 1]);
164→
165→          return (
166→            <div
167→              key={i}
168→              style={{
169→                fontFamily: fontFamily.display,
170→                fontSize: isLast ? 52 : 48,
171→                fontWeight: 700,
172→                color: isLast ? colors.green : colors.text,
173→                opacity,
174→                transform: `translateY(${translateY}px) scale(${scale})`,
175→                textAlign: "center",
176→              }}
177→            >
178→              {tagline}
179→            </div>
180→          );
181→        })}
182→      </div>
183→    </AbsoluteFill>
184→  );
185→};
186→
187→const DevLogLogo: React.FC<{ frame: number; fps: number }> = ({
188→  frame,
189→  fps,
190→}) => {
191→  const springVal = spring({
192→    frame: frame - 180,
193→    fps,
194→    config: { damping: 20, stiffness: 200 },
195→  });
196→
197→  const scale = interpolate(springVal, [0, 1], [0.5, 1]);
198→  const opacity = springVal;
199→
200→  return (
201→    <div
202→      style={{
203→        position: "absolute",
204→        top: "42%",
205→        transform: `translateY(-50%) scale(${scale})`,
206→        opacity,
207→      }}
208→    >
209→      <span
210→        style={{
211→          fontFamily: fontFamily.display,
212→          fontSize: 96,
213→          fontWeight: 700,
214→          color: colors.green,
215→        }}
216→      >
217→        DevLog
218→      </span>
219→    </div>
220→  );
221→};
222→
223→// Radial green glow that pulses behind the logo
224→const GreenGlow: React.FC<{ frame: number; fps: number }> = ({
225→  frame,
226→  fps,
227→}) => {
228→  const entrance = spring({
229→    frame: frame - 180,
230→    fps,
231→    config: { damping: 200 },
232→  });
233→
234→  const elapsed = frame - 180;
235→  // Pulse: oscillate between 0.15 and 0.35 opacity
236→  const pulsePhase = Math.sin(elapsed * 0.08) * 0.5 + 0.5;
237→  const glowOpacity = entrance * interpolate(pulsePhase, [0, 1], [0.12, 0.3]);
238→
239→  // Subtle scale breathing
240→  const breathe = 1 + Math.sin(elapsed * 0.06) * 0.05;
241→
242→  return (
243→    <div
244→      style={{
245→        position: "absolute",
246→        top: "50%",
247→        left: "50%",
248→        width: 600,
249→        height: 600,
250→        transform: `translate(-50%, -55%) scale(${breathe})`,
251→        borderRadius: "50%",
252→        background: `radial-gradient(circle, ${colors.green}40 0%, ${colors.green}10 40%, transparent 70%)`,
253→        opacity: glowOpacity,
254→        filter: "blur(40px)",
255→      }}
256→    />
257→  );
258→};
259→
260→// More interesting pulsing green cursor/bar animation
261→const PulsingCursor: React.FC<{ frame: number }> = ({ frame }) => {
262→  const elapsed = frame - 215;
263→
264→  // Glow intensity oscillates
265→  const glowIntensity = Math.sin(elapsed * 0.25) * 0.5 + 0.5;
266→  const barOpacity = interpolate(glowIntensity, [0, 1], [0.3, 1]);
267→  const barWidth = interpolate(glowIntensity, [0, 1], [20, 35]);
268→  const glowSize = interpolate(glowIntensity, [0, 1], [2, 12]);
269→
270→  return (
271→    <div
272→      style={{
273→        width: barWidth,
274→        height: 3,
275→        backgroundColor: colors.green,
276→        opacity: barOpacity,
277→        borderRadius: 2,
278→        boxShadow: `0 0 ${glowSize}px ${colors.green}, 0 0 ${glowSize * 2}px ${colors.green}40`,
279→      }}
280→    />
281→  );
282→};
