import React from "react";
import {
  AbsoluteFill,
  useCurrentFrame,
  interpolate,
} from "remotion";
import { colors } from "../design/colors";
import { SlackUI } from "../components/SlackUI";
import { SlackMessage } from "../components/SlackMessage";
import { TypingIndicator } from "../components/TypingIndicator";

// Scene 1: 270 frames (9s)
// Message is ~95 chars at 0.8 chars/frame → finishes at ~frame 25+119=144
// - Frames 0-20: Black → Slack UI fade-in (zoomed in)
// - Frames 25-145: Manager message typewriters in (bigger font)
// - Frames 145-235: 3 second hold so user can read (90 frames)
// - Frames 190-235: Typing indicator blinks (no reply)
// - Frames 235-260: Slow zoom into message
// - Frames 260-270: Hard cut to black

export const Scene1Slack: React.FC = () => {
  const frame = useCurrentFrame();

  // Fade in
  const fadeIn = interpolate(frame, [0, 20], [0, 1], {
    extrapolateLeft: "clamp",
    extrapolateRight: "clamp",
  });

  // Start zoomed in (1.25 base scale) with subtle entrance
  const scaleEntrance = interpolate(frame, [0, 20], [1.2, 1.25], {
    extrapolateLeft: "clamp",
    extrapolateRight: "clamp",
  });

  // Slow zoom toward end
  const zoomIn = interpolate(frame, [235, 260], [1.0, 1.1], {
    extrapolateLeft: "clamp",
    extrapolateRight: "clamp",
  });

  const scale = scaleEntrance * zoomIn;

  // Hard cut to black at end
  const cutToBlack = frame >= 260 ? 0 : 1;

  const opacity = fadeIn * cutToBlack;

  return (
    <AbsoluteFill
      style={{
        backgroundColor: colors.black,
        justifyContent: "center",
        alignItems: "center",
      }}
    >
      <div
        style={{
          opacity,
          transform: `scale(${scale})`,
        }}
      >
        <SlackUI>
          <SlackMessage
            name="Sarah Chen"
            avatar="SC"
            text="Performance review today. Let's go through what you've been doing for the last 3 months."
            startFrame={25}
            charsPerFrame={0.8}
            avatarColor={colors.slackYellow}
            fontSize={20}
          />
          <TypingIndicator startFrame={190} endFrame={235} />
        </SlackUI>
      </div>
    </AbsoluteFill>
  );
};

