import React from "react";
import {
  AbsoluteFill,
  useCurrentFrame,
  useVideoConfig,
  spring,
  interpolate,
} from "remotion";
import { colors } from "../design/colors";
import { fontFamily } from "../design/fonts";
import { Terminal } from "../components/Terminal";
import { TerminalLine } from "../components/TerminalLine";
import { MarkdownPreview } from "../components/MarkdownPreview";

// Scene 3: 390 frames (13s) — +60 frames from original for worklog viewing
// - Frames 0-25: Terminal fades in (spring)
// - Frames 25-55: $ npm install -g @ishaan812/devlog
// - Frames 65-85: $ devlog onboard
// - Frames 95-145: $ devlog ingest + sub-lines
// - Frames 145-175: Green output
// - Frames 185-220: $ devlog worklog --days 90
// - Frames 230-390: Markdown summary slides in prominently, holds for viewing

export const Scene3DevLog: React.FC = () => {
  const frame = useCurrentFrame();
  const { fps } = useVideoConfig();

  // Terminal entrance spring
  const entrance = spring({
    frame,
    fps,
    config: { damping: 200 },
  });
  const terminalOpacity = entrance;
  const terminalScale = interpolate(entrance, [0, 1], [0.95, 1]);

  // Show markdown after frame 230
  const showMarkdown = frame >= 230;

  // When worklog appears, terminal slides left and shrinks, worklog takes focus
  const terminalShift = showMarkdown
    ? interpolate(frame, [230, 260], [0, -40], {
        extrapolateLeft: "clamp",
        extrapolateRight: "clamp",
      })
    : 0;

  const worklogScale = showMarkdown
    ? spring({
        frame: frame - 230,
        fps,
        config: { damping: 200 },
      })
    : 0;

  return (
    <AbsoluteFill
      style={{
        backgroundColor: colors.bg,
        justifyContent: "center",
        alignItems: "center",
        padding: "40px 60px",
      }}
    >
      <div
        style={{
          display: "flex",
          gap: 40,
          alignItems: "flex-start",
          opacity: terminalOpacity,
          transform: `scale(${terminalScale})`,
        }}
      >
        {/* Terminal — slides left when worklog appears */}
        <div
          style={{
            transform: `translateX(${terminalShift}px)`,
            flexShrink: 0,
          }}
        >
          <Terminal>
            <TerminalLine
              text="npm install -g @ishaan812/devlog"
              startFrame={25}
              charsPerFrame={0.6}
            />

            {frame >= 55 && (
              <div
                style={{
                  fontFamily: fontFamily.mono,
                  fontSize: 14,
                  color: colors.secondary,
                  lineHeight: 1.6,
                }}
              >
                added 1 package in 2.1s
              </div>
            )}

            <TerminalLine
              text="devlog onboard"
              startFrame={65}
              charsPerFrame={0.6}
            />

            {frame >= 82 && (
              <div
                style={{
                  fontFamily: fontFamily.mono,
                  fontSize: 14,
                  color: colors.green,
                  lineHeight: 1.6,
                }}
              >
                {frame >= 85 && "✓ Database initialized\n"}
                {frame >= 88 && "✓ Configuration saved"}
              </div>
            )}

            <TerminalLine
              text="devlog ingest --all"
              startFrame={95}
              charsPerFrame={0.6}
            />

            {frame >= 115 && (
              <IngestOutput frame={frame} startFrame={115} />
            )}

            {frame >= 145 && (
              <div
                style={{
                  fontFamily: fontFamily.mono,
                  fontSize: 16,
                  color: colors.green,
                  fontWeight: 700,
                  lineHeight: 1.8,
                  marginTop: 4,
                }}
              >
                ✓ Ingested 2,847 commits across 5 repositories
              </div>
            )}

            <TerminalLine
              text="devlog worklog --days 90"
              startFrame={185}
              charsPerFrame={0.5}
            />

            {frame >= 220 && (
              <div
                style={{
                  fontFamily: fontFamily.mono,
                  fontSize: 14,
                  color: colors.secondary,
                  lineHeight: 1.6,
                }}
              >
                Generating work summary...
              </div>
            )}
          </Terminal>
        </div>

        {/* Markdown preview — larger and more prominent */}
        {showMarkdown && (
          <div
            style={{
              transform: `scale(${interpolate(worklogScale, [0, 1], [0.9, 1])})`,
              opacity: worklogScale,
            }}
          >
            <MarkdownPreview startFrame={0} />
          </div>
        )}
      </div>

      {/* Highlight border glow around worklog when it appears */}
      {frame >= 250 && (
        <WorklogHighlight frame={frame} />
      )}
    </AbsoluteFill>
  );
};

const IngestOutput: React.FC<{ frame: number; startFrame: number }> = ({
  frame,
  startFrame,
}) => {
  const repos = [
    "Scanning payments-api...",
    "Scanning auth-service...",
    "Scanning frontend-app...",
    "Scanning infrastructure...",
    "Scanning shared-libs...",
  ];

  return (
    <div
      style={{
        fontFamily: fontFamily.mono,
        fontSize: 14,
        color: colors.secondary,
        lineHeight: 1.6,
      }}
    >
      {repos.map((repo, i) => {
        const showFrame = startFrame + i * 5;
        if (frame < showFrame) return null;
        return <div key={i}>{repo}</div>;
      })}
    </div>
  );
};

// Subtle pulsing green glow that draws attention to the worklog area
const WorklogHighlight: React.FC<{ frame: number }> = ({ frame }) => {
  const elapsed = frame - 250;
  const pulse = Math.sin(elapsed * 0.1) * 0.5 + 0.5;
  const glowOpacity = interpolate(
    elapsed,
    [0, 15],
    [0, 0.15],
    { extrapolateLeft: "clamp", extrapolateRight: "clamp" },
  ) * interpolate(pulse, [0, 1], [0.6, 1]);

  return (
    <div
      style={{
        position: "absolute",
        right: 140,
        top: "50%",
        width: 620,
        height: 500,
        transform: "translateY(-50%)",
        borderRadius: 14,
        border: `1px solid ${colors.green}30`,
        boxShadow: `0 0 30px ${colors.green}15, inset 0 0 30px ${colors.green}08`,
        opacity: glowOpacity,
        pointerEvents: "none",
      }}
    />
  );
};

