import React from "react";
import {
  AbsoluteFill,
  useCurrentFrame,
  useVideoConfig,
  spring,
  interpolate,
} from "remotion";
import { colors } from "../design/colors";
import { fontFamily } from "../design/fonts";
import { Terminal } from "../components/Terminal";
import { TerminalLine } from "../components/TerminalLine";
import { MarkdownPreview } from "../components/MarkdownPreview";
import { CursorBlink } from "../components/CursorBlink";

// Scene 3: 330 frames (13-24s)
// - Frames 0-25: Terminal fades in (spring)
// - Frames 25-55: $ npm install -g @ishaan812/devlog
// - Frames 65-85: $ devlog onboard
// - Frames 95-145: $ devlog ingest + sub-lines
// - Frames 145-175: Green output
// - Frames 185-220: $ devlog worklog --days 90
// - Frames 230-330: Markdown summary slides in

export const Scene3DevLog: React.FC = () => {
  const frame = useCurrentFrame();
  const { fps } = useVideoConfig();

  // Terminal entrance spring
  const entrance = spring({
    frame,
    fps,
    config: { damping: 200 },
  });
  const terminalOpacity = entrance;
  const terminalScale = interpolate(entrance, [0, 1], [0.95, 1]);

  // Show markdown after frame 230
  const showMarkdown = frame >= 230;

  return (
    <AbsoluteFill
      style={{
        backgroundColor: colors.bg,
        justifyContent: "center",
        alignItems: "center",
        padding: "40px 60px",
      }}
    >
      <div
        style={{
          display: "flex",
          gap: 40,
          alignItems: "flex-start",
          opacity: terminalOpacity,
          transform: `scale(${terminalScale})`,
        }}
      >
        <Terminal>
          {/* npm install */}
          <TerminalLine
            text="npm install -g @ishaan812/devlog"
            startFrame={25}
            charsPerFrame={0.6}
          />

          {/* Install output */}
          {frame >= 55 && (
            <div
              style={{
                fontFamily: fontFamily.mono,
                fontSize: 14,
                color: colors.secondary,
                lineHeight: 1.6,
              }}
            >
              added 1 package in 2.1s
            </div>
          )}

          {/* devlog onboard */}
          <TerminalLine
            text="devlog onboard"
            startFrame={65}
            charsPerFrame={0.6}
          />

          {/* Onboard output */}
          {frame >= 82 && (
            <div
              style={{
                fontFamily: fontFamily.mono,
                fontSize: 14,
                color: colors.green,
                lineHeight: 1.6,
              }}
            >
              {frame >= 85 && "✓ Database initialized\n"}
              {frame >= 88 && "✓ Configuration saved"}
            </div>
          )}

          {/* devlog ingest */}
          <TerminalLine
            text="devlog ingest --all"
            startFrame={95}
            charsPerFrame={0.6}
          />

          {/* Ingest sub-lines */}
          {frame >= 115 && (
            <IngestOutput frame={frame} startFrame={115} />
          )}

          {/* Green success output */}
          {frame >= 145 && (
            <div
              style={{
                fontFamily: fontFamily.mono,
                fontSize: 16,
                color: colors.green,
                fontWeight: 700,
                lineHeight: 1.8,
                marginTop: 4,
              }}
            >
              ✓ Ingested 2,847 commits across 5 repositories
            </div>
          )}

          {/* devlog worklog */}
          <TerminalLine
            text="devlog worklog --days 90"
            startFrame={185}
            charsPerFrame={0.5}
          />

          {/* Generating output */}
          {frame >= 220 && (
            <div
              style={{
                fontFamily: fontFamily.mono,
                fontSize: 14,
                color: colors.secondary,
                lineHeight: 1.6,
              }}
            >
              Generating work summary...
            </div>
          )}
        </Terminal>

        {/* Markdown preview slides in from right */}
        {showMarkdown && <MarkdownPreview startFrame={0} />}
      </div>
    </AbsoluteFill>
  );
};

const IngestOutput: React.FC<{ frame: number; startFrame: number }> = ({
  frame,
  startFrame,
}) => {
  const repos = [
    "Scanning payments-api...",
    "Scanning auth-service...",
    "Scanning frontend-app...",
    "Scanning infrastructure...",
    "Scanning shared-libs...",
  ];

  return (
    <div
      style={{
        fontFamily: fontFamily.mono,
        fontSize: 14,
        color: colors.secondary,
        lineHeight: 1.6,
      }}
    >
      {repos.map((repo, i) => {
        const showFrame = startFrame + i * 5;
        if (frame < showFrame) return null;
        return (
          <div key={i}>
            {repo}
          </div>
        );
      })}
    </div>
  );
};

