import React from "react";
import { AbsoluteFill } from "remotion";
import { TransitionSeries } from "@remotion/transitions";
import { Scene1Slack } from "./scenes/Scene1Slack";
import { Scene2Chaos } from "./scenes/Scene2Chaos";
import { Scene3DevLog } from "./scenes/Scene3DevLog";
import { Scene4Reversal } from "./scenes/Scene4Reversal";
import { Scene5Final } from "./scenes/Scene5Final";
import { AudioLayer } from "./audio/AudioLayer";

// Total: 1710 frames = 57s at 30fps
// Scene 1: 270 frames (0-9s)     — Slack manager message, zoomed in, 3s hold
// Scene 2: 360 frames (9-21s)    — Stacking stats, chaos, git log
// Scene 3: 330 frames (21-32s)   — Terminal demo
// Scene 4: 480 frames (32-48s)   — Worklog paste, promotion, confetti
// Scene 5: 270 frames (48-57s)   — Stacking taglines, DevLog logo

export const Video: React.FC = () => {
  return (
    <AbsoluteFill style={{ backgroundColor: "#000000" }}>
      <TransitionSeries>
        <TransitionSeries.Sequence durationInFrames={270}>
          <Scene1Slack />
        </TransitionSeries.Sequence>
        <TransitionSeries.Sequence durationInFrames={360}>
          <Scene2Chaos />
        </TransitionSeries.Sequence>
        <TransitionSeries.Sequence durationInFrames={330}>
          <Scene3DevLog />
        </TransitionSeries.Sequence>
        <TransitionSeries.Sequence durationInFrames={480}>
          <Scene4Reversal />
        </TransitionSeries.Sequence>
        <TransitionSeries.Sequence durationInFrames={270}>
          <Scene5Final />
        </TransitionSeries.Sequence>
      </TransitionSeries>
      <AudioLayer />
    </AbsoluteFill>
  );
};

