import React from "react";
import { Sequence, staticFile, interpolate } from "remotion";
import { Audio } from "@remotion/media";

// Audio orchestrator — all sounds managed here
// Scene offsets:
//   Scene 1 starts at frame 0    (270 frames)
//   Scene 2 starts at frame 270  (360 frames)
//   Scene 3 starts at frame 630  (390 frames)
//   Scene 4 starts at frame 1020 (480 frames)
//   Scene 5 starts at frame 1500 (330 frames) — silent

export const AudioLayer: React.FC = () => {
  return (
    <>
      {/* Scene 1: Slack notification at frame 5 */}
      <Sequence from={5} durationInFrames={30} layout="none">
        <Audio src={staticFile("sounds/slack-notification.mp3")} volume={0.7} />
      </Sequence>

      {/* Scene 1-2: Ambient hum fades in at frame 10, runs through scene 2 */}
      <Sequence from={10} durationInFrames={620} layout="none">
        <Audio
          src={staticFile("sounds/ambient-hum.mp3")}
          loop
          volume={(f) =>
            interpolate(f, [0, 30, 590, 620], [0, 0.15, 0.15, 0], {
              extrapolateLeft: "clamp",
              extrapolateRight: "clamp",
            })
          }
        />
      </Sequence>

      {/* Scene 2: Fast typing during stacking stats (scene 2 starts at 270) */}
      <Sequence from={280} durationInFrames={120} layout="none">
        <Audio
          src={staticFile("sounds/typing-fast.mp3")}
          volume={(f) =>
            interpolate(f, [0, 10, 100, 120], [0, 0.3, 0.3, 0], {
              extrapolateLeft: "clamp",
              extrapolateRight: "clamp",
            })
          }
        />
      </Sequence>

      {/* Scene 2: Notification stack during chaos */}
      <Sequence from={420} durationInFrames={60} layout="none">
        <Audio
          src={staticFile("sounds/notification-stack.mp3")}
          volume={0.4}
        />
      </Sequence>

      {/* Scene 3: Soft typing during terminal demo (scene 3 starts at 630) */}
      <Sequence from={650} durationInFrames={340} layout="none">
        <Audio
          src={staticFile("sounds/typing-soft.mp3")}
          loop
          volume={(f) =>
            interpolate(f, [0, 15, 320, 340], [0, 0.25, 0.25, 0], {
              extrapolateLeft: "clamp",
              extrapolateRight: "clamp",
            })
          }
        />
      </Sequence>

      {/* Scene 4: Soft typing for worklog paste (scene 4 starts at 1020) */}
      <Sequence from={1030} durationInFrames={120} layout="none">
        <Audio
          src={staticFile("sounds/typing-soft.mp3")}
          volume={0.2}
        />
      </Sequence>

      {/* Scene 4: Send message whoosh when worklog posts */}
      <Sequence from={1070} durationInFrames={30} layout="none">
        <Audio src={staticFile("sounds/send-message.mp3")} volume={0.5} />
      </Sequence>

      {/* Scene 4: Slack notification for promotion message (1020+260) */}
      <Sequence from={1280} durationInFrames={30} layout="none">
        <Audio src={staticFile("sounds/slack-notification.mp3")} volume={0.6} />
      </Sequence>

      {/* Scene 5: Silent — no audio */}
    </>
  );
};

