import React from "react";
import {
  AbsoluteFill,
  Sequence,
  useCurrentFrame,
  useVideoConfig,
  spring,
  interpolate,
} from "remotion";
import { colors } from "../design/colors";
import { fontFamily } from "../design/fonts";

// Scene 5: 330 frames (11s)
// Phase 1 (0-200): Taglines stack up one by one (5 items now), pause
// Phase 2 (200-210): Fade out taglines
// Phase 3 (210-330): DevLog screen (~4s)
//   - 210: "DevLog" snappy spring
//   - 225: "Open-source. Local-first."
//   - 238: "by @ishaan812"
//   - 250: npm install box
//   - 268: "find out more at devlog.ishaan812.com"
//   - 250-315: Pulsing green glow
//   - 315-330: Fade to black

const TAGLINES = [
  "Too many repos.",
  "Too many branches.",
  "Too many pull requests.",
  "Too much data to keep track of.",
  "Stop forgetting what you shipped.",
];

const TAGLINE_INTERVAL = 28;

export const Scene5Final: React.FC = () => {
  const frame = useCurrentFrame();
  const { fps } = useVideoConfig();

  // Final fade to black
  const fadeOut = interpolate(frame, [315, 330], [1, 0], {
    extrapolateLeft: "clamp",
    extrapolateRight: "clamp",
  });

  return (
    <AbsoluteFill
      style={{
        backgroundColor: colors.black,
        justifyContent: "center",
        alignItems: "center",
        opacity: fadeOut,
      }}
    >
      {/* Phase 1: Stacking taglines (5 items × 28 = 140 frames + hold) */}
      <Sequence from={5} durationInFrames={205} layout="none">
        <StackingTaglines />
      </Sequence>

      {/* Phase 3: DevLog screen */}
      {frame >= 210 && (
        <>
          <GreenGlow frame={frame} fps={fps} />

          <DevLogLogo frame={frame} fps={fps} />

          {/* Open-source. Local-first. */}
          <div
            style={{
              position: "absolute",
              top: "52%",
              opacity: interpolate(frame, [225, 236], [0, 1], {
                extrapolateLeft: "clamp",
                extrapolateRight: "clamp",
              }),
            }}
          >
            <span
              style={{
                fontFamily: fontFamily.display,
                fontSize: 24,
                color: colors.secondary,
              }}
            >
              Open-source. Local-first.
            </span>
          </div>

          {/* by @ishaan812 */}
          <div
            style={{
              position: "absolute",
              top: "57%",
              opacity: interpolate(frame, [238, 248], [0, 1], {
                extrapolateLeft: "clamp",
                extrapolateRight: "clamp",
              }),
            }}
          >
            <span
              style={{
                fontFamily: fontFamily.mono,
                fontSize: 18,
                color: colors.green,
              }}
            >
              by @ishaan812
            </span>
          </div>

          {/* npm install box */}
          {frame >= 250 && <InstallBox frame={frame} fps={fps} />}

          {/* find out more URL */}
          <div
            style={{
              position: "absolute",
              bottom: 80,
              opacity: interpolate(frame, [268, 280], [0, 1], {
                extrapolateLeft: "clamp",
                extrapolateRight: "clamp",
              }),
            }}
          >
            <span
              style={{
                fontFamily: fontFamily.display,
                fontSize: 18,
                color: colors.secondary,
              }}
            >
              find out more at{" "}
              <span style={{ color: colors.green }}>
                devlog.ishaan812.com
              </span>
            </span>
          </div>

          {/* Pulsing cursor */}
          {frame >= 265 && frame < 315 && (
            <div style={{ position: "absolute", top: "80%" }}>
              <PulsingCursor frame={frame} />
            </div>
          )}
        </>
      )}
    </AbsoluteFill>
  );
};

const StackingTaglines: React.FC = () => {
  const frame = useCurrentFrame();
  const { fps, durationInFrames } = useVideoConfig();

  const allFadeOut = interpolate(
    frame,
    [durationInFrames - 15, durationInFrames],
    [1, 0],
    { extrapolateLeft: "clamp", extrapolateRight: "clamp" },
  );

  return (
    <AbsoluteFill
      style={{
        justifyContent: "center",
        alignItems: "center",
        opacity: allFadeOut,
      }}
    >
      <div
        style={{
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          gap: 14,
        }}
      >
        {TAGLINES.map((tagline, i) => {
          const enterFrame = i * TAGLINE_INTERVAL;
          if (frame < enterFrame) return null;

          const isLast = i === TAGLINES.length - 1;
          const localFrame = frame - enterFrame;

          const springVal = spring({
            frame: localFrame,
            fps,
            config: { damping: 20, stiffness: 200 },
          });

          const opacity = springVal;
          const translateY = interpolate(springVal, [0, 1], [25, 0]);
          const scale = interpolate(springVal, [0, 1], [0.85, 1]);

          return (
            <div
              key={i}
              style={{
                fontFamily: fontFamily.display,
                fontSize: isLast ? 48 : 44,
                fontWeight: 700,
                color: isLast ? colors.green : colors.text,
                opacity,
                transform: `translateY(${translateY}px) scale(${scale})`,
                textAlign: "center",
              }}
            >
              {tagline}
            </div>
          );
        })}
      </div>
    </AbsoluteFill>
  );
};

const DevLogLogo: React.FC<{ frame: number; fps: number }> = ({
  frame,
  fps,
}) => {
  const springVal = spring({
    frame: frame - 210,
    fps,
    config: { damping: 20, stiffness: 200 },
  });

  const scale = interpolate(springVal, [0, 1], [0.5, 1]);
  const opacity = springVal;

  return (
    <div
      style={{
        position: "absolute",
        top: "32%",
        transform: `translateY(-50%) scale(${scale})`,
        opacity,
      }}
    >
      <span
        style={{
          fontFamily: fontFamily.display,
          fontSize: 96,
          fontWeight: 700,
          color: colors.green,
        }}
      >
        DevLog
      </span>
    </div>
  );
};

const InstallBox: React.FC<{ frame: number; fps: number }> = ({
  frame,
  fps,
}) => {
  const springVal = spring({
    frame: frame - 250,
    fps,
    config: { damping: 200 },
  });

  const opacity = springVal;
  const translateY = interpolate(springVal, [0, 1], [15, 0]);

  return (
    <div
      style={{
        position: "absolute",
        top: "64%",
        opacity,
        transform: `translateY(${translateY}px)`,
      }}
    >
      <div
        style={{
          backgroundColor: "#1a1a1a",
          border: "1px solid #333",
          borderRadius: 10,
          padding: "18px 36px",
          display: "flex",
          alignItems: "center",
          gap: 12,
        }}
      >
        <span
          style={{
            fontFamily: fontFamily.mono,
            fontSize: 14,
            color: colors.secondary,
          }}
        >
          $
        </span>
        <span
          style={{
            fontFamily: fontFamily.mono,
            fontSize: 20,
            color: colors.green,
            fontWeight: 700,
          }}
        >
          npm install -g @ishaan812/devlog
        </span>
      </div>
    </div>
  );
};

const GreenGlow: React.FC<{ frame: number; fps: number }> = ({
  frame,
  fps,
}) => {
  const entrance = spring({
    frame: frame - 210,
    fps,
    config: { damping: 200 },
  });

  const elapsed = frame - 210;
  const pulsePhase = Math.sin(elapsed * 0.08) * 0.5 + 0.5;
  const glowOpacity =
    entrance * interpolate(pulsePhase, [0, 1], [0.12, 0.3]);
  const breathe = 1 + Math.sin(elapsed * 0.06) * 0.05;

  return (
    <div
      style={{
        position: "absolute",
        top: "38%",
        left: "50%",
        width: 600,
        height: 600,
        transform: `translate(-50%, -55%) scale(${breathe})`,
        borderRadius: "50%",
        background: `radial-gradient(circle, ${colors.green}40 0%, ${colors.green}10 40%, transparent 70%)`,
        opacity: glowOpacity,
        filter: "blur(40px)",
      }}
    />
  );
};

const PulsingCursor: React.FC<{ frame: number }> = ({ frame }) => {
  const elapsed = frame - 265;

  const glowIntensity = Math.sin(elapsed * 0.25) * 0.5 + 0.5;
  const barOpacity = interpolate(glowIntensity, [0, 1], [0.3, 1]);
  const barWidth = interpolate(glowIntensity, [0, 1], [20, 35]);
  const glowSize = interpolate(glowIntensity, [0, 1], [2, 12]);

  return (
    <div
      style={{
        width: barWidth,
        height: 3,
        backgroundColor: colors.green,
        opacity: barOpacity,
        borderRadius: 2,
        boxShadow: `0 0 ${glowSize}px ${colors.green}, 0 0 ${glowSize * 2}px ${colors.green}40`,
      }}
    />
  );
};

