  const barOpacity = interpolate(glowIntensity, [0, 1], [0.3, 1]);
  const barWidth = interpolate(glowIntensity, [0, 1], [20, 35]);
  const glowSize = interpolate(glowIntensity, [0, 1], [2, 12]);

  return (
    <div
      style={{
        width: barWidth,
        height: 3,
        backgroundColor: colors.green,
        opacity: barOpacity,
        borderRadius: 2,
        boxShadow: `0 0 ${glowSize}px ${colors.green}, 0 0 ${glowSize * 2}px ${colors.green}40`,
      }}
    />
  );
};

